(* Main Program *)
ht := 0;
NoResponses := 0;
call Initialize(); (* initialize global variables *)
while NoResponses < K do
ht := ht + 1;
call CollectResponses(ht); (* collect responses from all nodes of distance ht *)
end
procedure Initialize()
(* initialize network topology consisting of source and its neighbors *)
source := 0; (* node 0 is the source *)
Responses := null;
SetNodes := neighbor(source);
NewNodes := fsourceg;
distance(source) := 0;
for node x 2 SetNodes do
distance(x) := 1;
neighbor(x) := fsourceg;
NewNodes := NewNodes fxg;
procedure CollectResponses(ht)
(* collect responses from all nodes of distance ht *)
(* Description of Local Variables *)
(* 1. ht : distance of nodes whose responses are collected *)
(* 2. CurrentNodes : a set of nodes used in the current round of current phase *)
(* 3. curneighbor(i) : a set of node i's neighbors used in the current round *)
(* 4. Respondents : a set of all nodes of distance ht *)
(* 5. V isitNodes : a set of replied nodes of distance ht *)
(* 6. Tree : a tree built to collect responses from some nodes of distance ht *)
(* 7. NodesTree(i) : a set of nodes of depth i in Tree *)
(* 8. dests : a set of destinations of a Collect message *)
CurrentNodes := NewNodes;
for node x 2 CurrentNodes do
curneighbor(x) := neighbor(x);

Respondents := SetNodes;
SetNodes := ;;
V isitNodes := ;;
while V isitNodes 6 = Respondents do
call BuildTree(ht;Tree;NodesTree;CurrentNodes;curneighbor);
call FindReplySlot(ht;Tree; NodesTree;curneighbor);
dests := NodesTree(1);
send Collect(source; dests; Tree) to all neighbors;
call CollectReplies(ht; Tree; NodesTree); (* collect replies from all leaves of Tree *)
if NoResponses K then return;
V isitNodes := V isitNodes NodesTree(ht);
(* remove all leaves and internal nodes without ht-hop nodes as descendants *)
call RemoveNodes(ht;NodesTree;CurrentNodes;curneighbor);
end
procedure BuildTree(ht;Tree;NodesTree;CurrentNodes;curneighbor)
(* build a tree of height ht, a subgraph of current topology *)
(* Description of Local Variables *)
(* 1. ht : the height of the built tree *)
(* 2. Tree : a set of nodes in the built tree *)
(* 3. depth(x) : depth of node x 2 Tree *)
(* 4. parent(x) : parent of node x 2 Tree *)
(* 5. children(x) : a set of children of node x 2 Tree *)
(* 6. sibling(x) : a set of siblings of node x 2 Tree *)
(* 7. NodesTree(i) : a set of nodes of depth i in Tree *)
(* 8. CurrentNodes : a set of nodes used in the current round of current phase *)
(* 9. curneighbor(i) : a set of node i's neighbors used in the current round *)
(* 10. CNodes : possible internal nodes in the same depth *)
(* 11. Rate(x) : rate of node x *)
(* 12. Con ictNodes(x) : a set of nodes interfering with x *)
(* initialize a tree, Tree *)
Tree := fsourceg; (* source is the root of Tree *)
NodesTree(0) := fsourceg;
depth(source) := 0;
children(source) := curneighbor(source);
parent(source) := null;
if ht 1
then (* all current neighbors of source become a part of Tree *)
NodesTree(1) := ;;
for node x 2 curneighbor(source) do
Tree := Tree fxg;
NodesTree(1) := NodesTree(1) fxg;

depth(x) := 1;
parent(x) := source;
children(x) := ;;
sibling(x) := curneighbor(source);
else (* choose internal nodes of Tree, using rates of nodes *)
call ComputeRate(ht;CurrentNodes;curneighbor; Con ictNodes; children; Rate);
d := 1;
CNodes := curneighbor(source); (* possible internal nodes of depth 1 *)
while d < ht do (* include internal nodes depth by depth *)
call SelectNodesTree(d; curneighbor; CNodes; NNodes; Con ictNodes,
children; Rate; Tree; NodesTree);
d := d + 1; (* increment depth *)
CNodes := NNodes;
end
(* include leaf nodes that are children of node x in NodesTree(ht 1) *)
NodesTree(ht) := ;;
for node x 2 NodesTree(ht 1) do
for node y 2 children(x) do
Tree := Tree fyg;
NodesTree(ht) := NodesTree(ht) fyg;
parent(y) := x;
sibling(y) := children(x);
depth(y) := ht;
children(y) := ;;
procedure ComputeRate(ht;CurrentNodes;curneighbor;Con ictNodes; children; Rate)
(* compute Rate(x); Con ictNodes(x); children(x) for x 2 CurrentNodes *)
(* Description of Local Variables *)
(* 1. ht : the height of the tree *)
(* 2. CurrentNodes : a set of nodes used in the current round of current phase *)
(* 3. curneighbor(i) : a set of node i's neighbors used in the current round *)
(* 4. Con ictNodes(x) : a set of nodes interfering with x *)
(* 5. children(x) : a set of potential children of node x *)
(* 6. Rate(x) : rate of node x *)
(* 7. Resps(x) : the set of descendants of node x at distance ht *)
(* 8. CNodes; CNodes2 : a subset of CurrentNodes *)
d := ht;
CNodes := ;;
for node x 2 CurrentNodes do
if distance(x) > 0 and distance(x) < ht
then CNodes := CNodes fxg;
CNodes2 := CNodes;

while d > 1 do
d := d 1;
for node x 2 CNodes do
if distance(x) = d
then
children(x) := ;;
Con ictNodes(x) := ;;
for node y 2 curneighbor(x) do
if (distance(y) = distance(x) + 1)
then (* y is a potential child of x *)
children(x) := children(x) fyg;
for node z 2 curneighbor(y) do
if z 6 = x and distance(z) = d
then (* z interferes with x *)
Con ictNodes(x) := Con ictNodes(x) fzg;
(* compute rate of x *)
Resps(x) := ;;
if d < ht 1
then
for node y 2 children(x) do
Resps(x) := Resps(x) Resps(y);
else
Resps(x) := Resps(x) children(x);
Rate(x) := jResps(x)j;
CNodes2 := CNodes2 fxg;
CNodes := CNodes2;
procedure SelectNodesTree(d; curneighbor;CNodes; NNodes; Con ictNodes; children; Rate,
Tree; NodesTree)
(* select internal nodes of depth d with maximum rate *)
(* Description of Variables *)
(* 1. curneighbor(i) : a set of node i's neighbors used in the current round *)
(* 2. CNodes : a set of potential nodes of depth d in Tree *)
(* 3. NNodes : a set of potential nodes of depth d + 1 in Tree *)
(* 4. Con ictNodes(x) : a set of nodes interfering with x *)
(* 5. children(x) : a set of potential children of node x *)
(* 6. Rate(x) : rate of node x *)
(* 7. Tree : a set of nodes in the built tree *)
(* 8. NodesTree : a set of nodes in the same depth in Tree *)
NNodes := ;;
NodesTree(d) := ;; (* a set of nodes of depth d in Tree *)
while CNodes 6 = ; do (* are nodes to choose left? *)

x := FindMaxRate(CNodes;Rate); (* nd node with max rate among CNodes *)
Tree := Tree fxg;
NodesTree(d) := NodesTree(d) fxg;
depth(x) := d;
for node y 2 NodesTree(d 1) do (* nd the parent of x *)
if y 2 curneighbor(x)
then
parent(x) := y;
sibling(x) := children(y);
exit;
CNodes := CNodes fxg Con ictNodes(x); (* remove nodes interfering with x *)
NNodes := NNodes children(x); (* add all children of x *)
end
for node x 2 NodesTree(d) do (* update sibling(x) for node x of depth d *)
sibling(x) := sibling(x) \ NodesTree(d);
for node x 2 NodesTree(d 1) do (* update children(x) for node x of depth d 1 *)
children(x) := children(x) \ NodesTree(d);
function FindMaxRate(Nodes;Rate)
(* nd a node with maximum rate *)
(* Description of Local Variables *)
(* 1. Nodes : a set of nodes *)
(* 2. Rate(x) : rate of node x 2 Nodes *)
(* 3. maxrate : current maximum rate *)
(* 4. maxid : ID of node with maxrate *)
maxrate := 1;
maxid := 1;
for node x 2 Nodes do
if Rate(x) > maxrate
then
maxrate := Rate(x);
maxid := x;
else
if Rate(x) = maxrate
then (* tie is broken in favor of smaller ID *)
if maxid > x
then maxid := x;
return maxid;

