/////////////////////////////
// wormhole interface method
/////////////////////////////
int CGCNOWamTarget :: receiveWormData(PortHole& p) {
CGPortHole& cp = *(CGPortHole*)&p;
cp.forceSendData();
return TRUE;
}
// ---------------------------------------------------------------------------
int CGCNOWamTarget :: sendWormData(PortHole& p) {
CGPortHole& cp = *(CGPortHole*)&p;
cp.forceGrabData();
return TRUE;
}
